mapper.select:
select all_indexes.owner owner, all_indexes.index_name name, all_indexes.index_type type, all_indexes.uniqueness uniqueness, ( select LISTAGG(all_ind_columns.column_name, ',') WITHIN GROUP (ORDER BY column_position) from all_ind_columns where all_indexes.index_name=all_ind_columns.index_name and all_indexes.owner=all_ind_columns.index_owner )columns, all_indexes.tablespace_name tablespace, all_indexes.partitioned partitioning,
'{ "last_analyzed":"'||to_char(all_indexes.last_analyzed, 'YYYY-MM-DD"T"hh24:mi:ss')||'", '|| '"logging":"' ||all_indexes.logging||'", '|| '"temporary":"' ||all_indexes.temporary||'", '|| '"generated":"' ||all_indexes.generated||'", '|| '"secondary":"' ||all_indexes.secondary||'", '|| '"dropped":"' ||all_indexes.dropped||'", '|| '"visibility":"' ||all_indexes.visibility||'" } ' jsonDetails
from all_indexes where all_indexes.table_name=#{tableName} and all_indexes.table_owner=#{tableOwner}
